import React, { useEffect, useState } from "react";
import todo from '../ethereum/todo.js';
import web3 from '../ethereum/web3';
import { Grid } from 'semantic-ui-react';

const DisplayTasks = ({count}) => {


    const createTaskList = async() => {
        const taskRows = [];
        const tasks = [];

        let i;
        for (i = 1; i < count; i++){
            const task = await todo.methods.tasks(i).call();
            // console.log(task);
            tasks.push(task);
        }
        console.log(tasks)

        for (i = 1; i <= count; i++){

            if (i % 3 == 0) {
                const taskRow = (
                    <Grid.Row>
                        <Grid.Column>
                            <div className="ui left floated compact segment">
                                <div className="ui toggle checked checkbox">
                                    <input 
                                    type="checkbox" 
                                    name={tasks[i-3].id}
                                    onClick={() => checkTask(tasks[i-3].id)}
                                    ></input>
                                    <label>{tasks[i-3].content}</label>
                                </div>
                            </div>
                        </Grid.Column>
                        <Grid.Column>
                            <div className="ui left floated compact segment">
                                <div className="ui toggle checked checkbox">
                                    <input 
                                    type="checkbox" 
                                    name={tasks[i-2].id}
                                    onClick={() => checkTask(tasks[i-2].id)}
                                    ></input>
                                    <label>{tasks[i-2].content}</label>
                                </div>
                            </div>
                        </Grid.Column>
                        <Grid.Column>
                            <div className="ui left floated compact segment">
                                <div className="ui toggle checked checkbox">
                                    <input 
                                    type="checkbox" 
                                    name={tasks[i-1].id}
                                    onClick={() => checkTask(tasks[i-1].id)}
                                    ></input>
                                    <label>{tasks[i-1].content}</label>
                                </div>
                            </div>
                        </Grid.Column>
                    </Grid.Row>
                )
                taskRows.push(taskRow);
                console.log(taskRows[0]);
            }
        }
        const mod = i % 3;
        if (mod != 3){
            if (mod == 1){
                const taskRow = (
                    <Grid.Row>
                        <Grid.Column>
                            <div className="ui left floated compact segment">
                                <div className="ui toggle checked checkbox">
                                    <input 
                                    type="checkbox" 
                                    name={tasks[i-1].id}
                                    onClick={() => checkTask(tasks[i-1].id)}
                                    ></input>
                                    <label>{tasks[i-1].content}</label>
                                </div>
                            </div>
                        </Grid.Column>
                    </Grid.Row>
                )
                taskRows.push(taskRow);
            }
            else {
                const taskRow = (
                    <Grid.Row>
                        <Grid.Column>
                            <div className="ui left floated compact segment">
                                <div className="ui toggle checked checkbox">
                                    <input 
                                    type="checkbox" 
                                    name={tasks[i-2].id}
                                    onClick={() => checkTask(tasks[i-2].id)}
                                    ></input>
                                    <label>{tasks[i-2].content}</label>
                                </div>
                            </div>
                        </Grid.Column>
                        <Grid.Column>
                            <div className="ui left floated compact segment">
                                <div className="ui toggle checked checkbox">
                                    <input 
                                    type="checkbox" 
                                    name={tasks[i-1].id}
                                    onClick={() => checkTask(tasks[i-1].id)}
                                    ></input>
                                    <label>{tasks[i-1].content}</label>
                                </div>
                            </div>
                        </Grid.Column>
                    </Grid.Row>
                )
                taskRows.push(taskRow);
            }
        }
        return (
            <>
            {
                taskRows.map((task) => {
                    return (
                        {task}
                    )
                })
            }
            </>
        )
    }

    const checkTask = async(id) => {
        const accounts = await web3.eth.getAccounts();
        await todo.methods.checkTask(id).send({from: accounts[0]});
    }

    return (
        <Grid column={3} divided>
            <Grid.Row>
            <Grid.Column>
                <div className="ui left floated compact segment">
                    <div className="ui toggle checked checkbox">
                        <input 
                        type="checkbox" 
                        name="1"
                        onClick={() => checkTask(1)}
                        ></input>
                        <label>A Task</label>
                    </div>
                </div>
            </Grid.Column>
            </Grid.Row>
        </Grid>
    )
}

export default DisplayTasks;
