import React, { useEffect, useState } from "react";
import todo from '../ethereum/todo.js';
import web3 from '../ethereum/web3';
import { Grid } from 'semantic-ui-react';

const DisplayTasks = ({count}) => {

    const [tasks, setTasks] = useState([{id: 0}]);
    const [loading, setLoading] = useState(false);

    useEffect( () => {
        setLoading(true);
        createTaskList();
    },[])

    useEffect( () => {
        if (tasks[0].id === 1){
            createFormattedTaskList();
            setLoading(false);
        }
    }, [tasks])

    const checkTask = async(id) => {
        const accounts = await web3.eth.getAccounts();
        await todo.methods.checkTask(id).send({from: accounts[0]});
    }

    const createTaskList = async () => {
        // const taskRows = [];
        const initialTasks = [];
        initialTasks.push({id: 1});
        let i;
        for (i = 1; i < count; i++){
            const task = await todo.methods.tasks(i).call();
            initialTasks.push(task);          
        }
        setTasks(initialTasks);
    }

    const createFormattedTaskList = () => {

        const length = tasks.length;
        const mod = (length - 1) % 3;
        const taskRows = [];
        let i;
        for (i = 3; i < length; i += 3){
            taskRows.push(
                <Grid.Row>
                    <Grid.Column>
                        <div className="ui left floated compact segment">
                            <div className="ui toggle checked checkbox">
                                <input
                                type="checkbox"
                                name={tasks[i-2].id}
                                onClick={() => checkTask(tasks[i-2].id)}
                                ></input>
                                <label>{tasks[i-2].content}</label>
                            </div>
                        </div>
                    </Grid.Column>
                    <Grid.Column>
                        <div className="ui left floated compact segment">
                            <div className="ui toggle checked checkbox">
                                <input
                                type="checkbox"
                                name={tasks[i-1].id}
                                onClick={() => checkTask(tasks[i-1].id)}
                                ></input>
                                <label>{tasks[i-1].content}</label>
                            </div>
                        </div>
                    </Grid.Column>
                    <Grid.Column>
                        <div className="ui left floated compact segment">
                            <div className="ui toggle checked checkbox">
                                <input
                                type="checkbox"
                                name={tasks[i].id}
                                onClick={() => checkTask(tasks[i].id)}
                                ></input>
                                <label>{tasks[i].content}</label>
                            </div>
                        </div>
                    </Grid.Column>
                </Grid.Row>
               )
       }
       if (mod !== 0){
           if (mod === 1){
                taskRows.push(
                   <Grid.Row>
                       <Grid.Column>
                           <div className="ui left floated compact segment">
                               <div className="ui toggle checked checkbox">
                                   <input
                                   type="checkbox"
                                   name={tasks[length - 1].id}
                                   onClick={() => checkTask(tasks[length - 1].id)}
                                   ></input>
                                   <label>{tasks[length - 1].content}</label>
                               </div>
                           </div>
                       </Grid.Column>
                   </Grid.Row>
               )
           }
           else {
                taskRows.push(
                   <Grid.Row>
                       <Grid.Column>
                           <div className="ui left floated compact segment">
                               <div className="ui toggle checked checkbox">
                                   <input
                                   type="checkbox"
                                   name={tasks[length - 2].id}
                                   onClick={() => checkTask(tasks[length - 2].id)}
                                   ></input>
                                   <label>{tasks[length - 2].content}</label>
                               </div>
                           </div>
                       </Grid.Column>
                       <Grid.Column>
                           <div className="ui left floated compact segment">
                               <div className="ui toggle checked checkbox">
                                   <input
                                   type="checkbox"
                                   name={tasks[length - 1].id}
                                   onClick={() => checkTask(tasks[length - 1].id)}
                                   ></input>
                                   <label>{tasks[length - 1].content}</label>
                               </div>
                           </div>
                       </Grid.Column>
                   </Grid.Row>
               )
           }
       }
       console.log('task row');
       console.log(taskRows);
       setTasks(taskRows);
    }

    // const renderList = () => {
    //     for ()
    // }

    console.log('tasks')
    console.log(tasks);
    if (loading){
        return <h1>loading</h1>
    }

    else {
        const stringTasks = JSON.stringify(tasks);
        return (
            <Grid columns={3} divided>
                {tasks.map(task => {
                    const stringTask = JSON.stringify(task);
                    return (
                        React.createElement(stringTask)
                    );
                })}
            </Grid>
        )
    }

}

export default DisplayTasks;

