import React from 'react';
import todo from './ethereum/todo.js';
import web3 from './ethereum/web3.js';

class App extends React.Component {

    state = {address: '', errorMessage: '', tasks: []}

    async compoundDidMount() {
        this.createTasksList();
    }


    async createTasksList() {
        const count = await web3.methods.count().call();
        const tasksList = [];
        for (let i = 0; i <= count; i++) {
            const task = await web3.methods.tasks(i).call();
            tasksList.push(task);
        }
        console.log(tasksList[0]);
    }

    async createTask(content) {
        const accounts = await web3.eth.getAccounts();
        try {
            await todo.methods.createTask(content).send()
        }
        catch (err) {
            this.setState({errorMessage: err.message})
        }
    }

    render() {
        return (
            <h1>hello</h1>
        );
    }

}

export default App;
