import React, {useEffect, useState} from 'react';
import todo from './ethereum/todo.js';
import web3 from './ethereum/web3.js';
import DisplayTasks from './components/DisplayTasks';
import CreateTask from './components/CreateTask.js';
import DropDownList from './components/DropDown.js';
import Loader from './components/Loader'
import './css/stylesheet.css';


const App = () => {

    const [loading, setLoading] = useState(true);
    const [errMessage, setErrMessage] = useState([]);
    const [count, setCount] = useState(0);
    const [completed, setCompleted] = useState([]);

    useEffect(async() => {
        const initialCount = await todo.methods.count().call();
        setCount(initialCount);
        console.log('completed list');
    }, [])

    if (!loading){
        return (
            <div className="ui container">
                <DisplayTasks count={count} completed={completed} setCompleted={setCompleted}/>
                <CreateTask setCount={setCount} count={count}/>
                <br></br>
                <DropDownList completed={completed} />
            </div>
        )
    }

    return(
       <div className="loader">
           <Loader/>
       </div>
    )
}

export default App;
