import React, { useEffect, useState } from "react";
import todo from '../ethereum/todo.js'

const DisplayTasks = () => {

    const [task, setTask] = useState({})

    useEffect(async() => {
        const task = await todo.methods.tasks(1).call();
        setTask(task);
        console.log(task);
    }, [])

    const checkTask = async(id) => {
        await todo.methods.checkTask(id).send();
    }

    return (
        <div className="ui checkbox">
            <input 
            type="checkbox" 
            name={task.id}
            onClick={checkTask(task.id)}
            ></input>
            <label>{task.content}bonjour</label>
        </div>
    )
}

export default DisplayTasks;
