import React, { useEffect, useState } from "react";
import todo from '../ethereum/todo.js';
import web3 from '../ethereum/web3';
import { Grid } from 'semantic-ui-react';

const DisplayinitalTask = ({count}) => {

    const checkTask = async(id) => {
        const accounts = await web3.eth.getAccounts();
        await todo.methods.checkTask(id).send({from: accounts[0]});
    }

    const createTaskList = async() => {
        const taskRows = [];
        const initalTask = [];

        let i;
        for (i = 1; i < count; i++){
            const task = await todo.methods.initalTask(i).call();
            // console.log(task);
            initalTask.push(task);
        }
        console.log(initalTask)

        for (i = 1; i <= count; i++){

            if (i % 3 == 0) {
                const taskRow = (
                    <Grid.Row>
                        <Grid.Column>
                            <div className="ui left floated compact segment">
                                <div className="ui toggle checked checkbox">
                                    <input 
                                    type="checkbox" 
                                    name={initalTask[i-3].id}
                                    onClick={() => checkTask(initalTask[i-3].id)}
                                    ></input>
                                    <label>{initalTask[i-3].content}</label>
                                </div>
                            </div>
                        </Grid.Column>
                        <Grid.Column>
                            <div className="ui left floated compact segment">
                                <div className="ui toggle checked checkbox">
                                    <input 
                                    type="checkbox" 
                                    name={initalTask[i-2].id}
                                    onClick={() => checkTask(initalTask[i-2].id)}
                                    ></input>
                                    <label>{initalTask[i-2].content}</label>
                                </div>
                            </div>
                        </Grid.Column>
                        <Grid.Column>
                            <div className="ui left floated compact segment">
                                <div className="ui toggle checked checkbox">
                                    <input 
                                    type="checkbox" 
                                    name={initalTask[i-1].id}
                                    onClick={() => checkTask(initalTask[i-1].id)}
                                    ></input>
                                    <label>{initalTask[i-1].content}</label>
                                </div>
                            </div>
                        </Grid.Column>
                    </Grid.Row>
                )
                taskRows.push(taskRow);
                console.log(taskRows[0]);
            }
        }
        const mod = i % 3;
        if (mod != 3){
            if (mod == 1){
                const taskRow = (
                    <Grid.Row>
                        <Grid.Column>
                            <div className="ui left floated compact segment">
                                <div className="ui toggle checked checkbox">
                                    <input 
                                    type="checkbox" 
                                    name={initalTask[i-1].id}
                                    onClick={() => checkTask(initalTask[i-1].id)}
                                    ></input>
                                    <label>{initalTask[i-1].content}</label>
                                </div>
                            </div>
                        </Grid.Column>
                    </Grid.Row>
                )
                taskRows.push(taskRow);
            }
            else {
                const taskRow = (
                    <Grid.Row>
                        <Grid.Column>
                            <div className="ui left floated compact segment">
                                <div className="ui toggle checked checkbox">
                                    <input 
                                    type="checkbox" 
                                    name={initalTask[i-2].id}
                                    onClick={() => checkTask(initalTask[i-2].id)}
                                    ></input>
                                    <label>{initalTask[i-2].content}</label>
                                </div>
                            </div>
                        </Grid.Column>
                        <Grid.Column>
                            <div className="ui left floated compact segment">
                                <div className="ui toggle checked checkbox">
                                    <input 
                                    type="checkbox" 
                                    name={initalTask[i-1].id}
                                    onClick={() => checkTask(initalTask[i-1].id)}
                                    ></input>
                                    <label>{initalTask[i-1].content}</label>
                                </div>
                            </div>
                        </Grid.Column>
                    </Grid.Row>
                )
                taskRows.push(taskRow);
            }
        }
        return (
            <>
            {
                taskRows
            }
            </>
        )
    }

    return (
        <Grid column={3} divided>
            {createTaskList()}
        </Grid>
    )
}

export default DisplayinitalTask;
