import React, { useEffect, useState } from "react";
import todo from '../ethereum/todo.js';
import web3 from '../ethereum/web3';
import { Grid, Image } from 'semantic-ui-react';

const DisplayTasks = ({count}) => {

    const [taskList, setTaskList] = useState([]);

    const createTaskList = async() => {
        const taskRows = [];
        const tasks = [];

        let i;
        for (i = 1; i <= count; i++){
            const task = await todo.methods.tasks(i).call();
            tasks.push(task);
        }

        for (i = 1; i <= count; i++){

            if (i % 3 == 0) {
                const taskRow = (
                    <Grid.Row>
                        <Grid.Column>
                            <div className="ui left floated compact segment">
                                <div className="ui toggle checked checkbox">
                                    <input 
                                    type="checkbox" 
                                    name={tasks[i-2].id}
                                    onClick={() => checkTask(tasks[i-2].id)}
                                    ></input>
                                    <label>{tasks[i-2].content}</label>
                                </div>
                            </div>
                        </Grid.Column>
                        <Grid.Column>
                            <div className="ui left floated compact segment">
                                <div className="ui toggle checked checkbox">
                                    <input 
                                    type="checkbox" 
                                    name={tasks[i-1].id}
                                    onClick={() => checkTask(tasks[i].id)}
                                    ></input>
                                    <label>{tasks[i-1].content}</label>
                                </div>
                            </div>
                        </Grid.Column>
                        <Grid.Column>
                            <div className="ui left floated compact segment">
                                <div className="ui toggle checked checkbox">
                                    <input 
                                    type="checkbox" 
                                    name={tasks[i].id}
                                    onClick={() => checkTask(tasks[i].id)}
                                    ></input>
                                    <label>{tasks[i].content}</label>
                                </div>
                            </div>
                        </Grid.Column>
                    </Grid.Row>
                )
                taskRows.push(taskRow);
                console.log(taskRows);
            }
        }
        const mod = i % 3;
        if (mod != 3){
            if (mod == 1){
                const taskRow = (
                    <Grid.Row>
                        <Grid.Column>
                            <div className="ui left floated compact segment">
                                <div className="ui toggle checked checkbox">
                                    <input 
                                    type="checkbox" 
                                    name={tasks[i].id}
                                    onClick={() => checkTask(tasks[i].id)}
                                    ></input>
                                    <label>{tasks[i].content}</label>
                                </div>
                            </div>
                        </Grid.Column>
                    </Grid.Row>
                )
                taskRows.push(taskRow);
            }
            else {
                const taskRow = (
                    <Grid.Row>
                        <Grid.Column>
                            <div className="ui left floated compact segment">
                                <div className="ui toggle checked checkbox">
                                    <input 
                                    type="checkbox" 
                                    name={tasks[i-1].id}
                                    onClick={() => checkTask(tasks[i-1].id)}
                                    ></input>
                                    <label>{tasks[i-1].content}</label>
                                </div>
                            </div>
                        </Grid.Column>
                        <Grid.Column>
                            <div className="ui left floated compact segment">
                                <div className="ui toggle checked checkbox">
                                    <input 
                                    type="checkbox" 
                                    name={tasks[i].id}
                                    onClick={() => checkTask(tasks[i].id)}
                                    ></input>
                                    <label>{tasks[i].content}</label>
                                </div>
                            </div>
                        </Grid.Column>
                    </Grid.Row>
                )
                taskRows.push(taskRow);
            }
        }
        return (
            <>
            {
                taskRows.forEach((task) => {
                    return (
                        {task}
                    )
                })
            }
            </>
        )
    }

    const checkTask = async(id) => {
        const accounts = await web3.eth.getAccounts();
        await todo.methods.checkTask(id).send({from: accounts[0]});
    }

    return (
        <Grid column={3}>
            {createTaskList()}
        </Grid>
    )
}

export default DisplayTasks;
