import React from 'react';
import web3 from '../ethereum/web3';
import todo from '../ethereum/todo';
import { Button, Checkbox, Form } from 'semantic-ui-react';

const CreateTask = () => {

    const createTask = async(content) => {
        const accounts = await todo.methods.createTask(content).send({from: accounts[0]});
    }

    return (
        <Form>
            <Form.Field>
                <label>Create a Task</label>
                <input placeholder='I will brush my teeth' />
            </Form.Field>
            <Button type='submit'>Create</Button>
        </Form>
    )
}

export default CreateTask;
