import React, { useEffect, useState } from "react";
import todo from '../ethereum/todo.js';
import web3 from '../ethereum/web3';
import { Grid } from 'semantic-ui-react';

const DisplayTasks = ({count}) => {

    const [tasks, setTasks] = useState([{id: 0}]);

    useEffect(async() => {
        await createTaskList();
        await createFormattedTaskList();
    },[])

    const checkTask = async(id) => {
        const accounts = await web3.eth.getAccounts();
        await todo.methods.checkTask(id).send({from: accounts[0]});
    }

    const createTaskList = async () => {
        // const taskRows = [];
        const initialTasks = [];
        initialTasks.push({id: 0});
        console.log('starting desplay task')
        let i;
        for (i = 1; i < count; i++){
            const task = await todo.methods.tasks(i).call();
            console.log(task);
            initialTasks.push(task);          
        }
        console.log(initialTasks);
        setTasks(initialTasks);
    }

    const createFormattedTaskList = () => {
        console.log('starting formattedlist')

        const length = tasks.length;
        const mod = (length - 1) % 3;
        console.log(mod);
        const taskRows = [];
        let i;
        for (i = 3; i > length; i += 3){
            console.log('starting loop')
            let taskRow = (
                <Grid.Row>
                    <Grid.Column>
                        <div className="ui left floated compact segment">
                            <div className="ui toggle checked checkbox">
                                <input
                                type="checkbox"
                                name={tasks[i-2].id}
                                onClick={() => checkTask(tasks[i-2].id)}
                                ></input>
                                <label>{tasks[i-2].content}</label>
                            </div>
                        </div>
                    </Grid.Column>
                    <Grid.Column>
                        <div className="ui left floated compact segment">
                            <div className="ui toggle checked checkbox">
                                <input
                                type="checkbox"
                                name={tasks[i-1].id}
                                onClick={() => checkTask(tasks[i-1].id)}
                                ></input>
                                <label>{tasks[i-1].content}</label>
                            </div>
                        </div>
                    </Grid.Column>
                    <Grid.Column>
                        <div className="ui left floated compact segment">
                            <div className="ui toggle checked checkbox">
                                <input
                                type="checkbox"
                                name={tasks[i].id}
                                onClick={() => checkTask(tasks[i].id)}
                                ></input>
                                <label>{tasks[i].content}</label>
                            </div>
                        </div>
                    </Grid.Column>
                </Grid.Row>
               )
           console.log('the first task row');
           console.log(taskRow);
           taskRows.push(taskRow);
       }
       console.log(mod);
       if (mod !== 0){
           if (mod === 1){
                let taskRow = (
                   <Grid.Row>
                       <Grid.Column>
                           <div className="ui left floated compact segment">
                               <div className="ui toggle checked checkbox">
                                   <input
                                   type="checkbox"
                                   name={tasks[i].id}
                                   onClick={() => checkTask(tasks[i].id)}
                                   ></input>
                                   <label>{tasks[i].content}</label>
                               </div>
                           </div>
                       </Grid.Column>
                   </Grid.Row>
               )
               console.log(taskRow);
               taskRows.push(taskRow);
           }
           else {
                let taskRow = (
                   <Grid.Row>
                       <Grid.Column>
                           <div className="ui left floated compact segment">
                               <div className="ui toggle checked checkbox">
                                   <input
                                   type="checkbox"
                                   name={tasks[i-1].id}
                                   onClick={() => checkTask(tasks[i-1].id)}
                                   ></input>
                                   <label>{tasks[i-1].content}</label>
                               </div>
                           </div>
                       </Grid.Column>
                       <Grid.Column>
                           <div className="ui left floated compact segment">
                               <div className="ui toggle checked checkbox">
                                   <input
                                   type="checkbox"
                                   name={tasks[i].id}
                                   onClick={() => checkTask(tasks[i].id)}
                                   ></input>
                                   <label>{tasks[i].content}</label>
                               </div>
                           </div>
                       </Grid.Column>
                   </Grid.Row>
               )
               taskRows.push(taskRow);
           }
       }
       console.log(taskRows);
    }

    return (
        <div>
            {tasks.map((task, index) => {
                console.log(task);
                return (
                    <div key={index} className="ui container">
                        <div className="ui left floated compact segment">
                            <div className="ui toggle checked checkbox">
                                <input 
                                type="checkbox" 
                                name={task.id}
                                onClick={() => checkTask(task.id)}
                                ></input>
                                <label>{task.content}</label>
                            </div>
                        </div>
                    </ div>
                );
                })}
            hello this actually works
        </div>
    )
}

export default DisplayTasks;

